var worldDataProvider = {
  "map": "worldLow",
  "getAreasFromMap": true,
  "images": [ {
    "svgPath": backIconSVG,
    "id": "backButton",
    "label": "Back to continents map",
    "rollOverColor": "#9a7bca",
    "labelRollOverColor": "#9a7bca",
    "useTargetsZoomValues": true,
    "left": 30,
    "bottom": 30,
    "labelFontSize": 15,
    "selectable": true
  } ]
};



var continentsDataProvider = {
  "map": "continentsLow",

  "areas": [ {
    "id": "africa",
    "linkToObject": worldDataProvider,
    "color": "#605675",
    "passZoomValuesToTarget": true
  }, {
    "id": "asia",
    "linkToObject": worldDataProvider,
    "color": "#a791b4",
    "passZoomValuesToTarget": true
  }, {
    "id": "australia",
    "linkToObject": worldDataProvider,
    "color": "#7f7891",
    "passZoomValuesToTarget": true
  }, {
    "id": "europe",
    "linkToObject": worldDataProvider,
    "color": "#9186a2",
    "passZoomValuesToTarget": true
  }, {
    "id": "north_america",
    "linkToObject": worldDataProvider,
    "color": "#868191",
    "passZoomValuesToTarget": true
  }, {
    "id": "south_america",
    "linkToObject": worldDataProvider,
    "color": "#8f7ea9",
    "passZoomValuesToTarget": true
  } ]

};


var map = AmCharts.makeChart( "mapdiv", {
  "type": "map",
  "theme": "light",
  "projection": "winkel3",

  "areasSettings": {
    "autoZoom": true,
    "rollOverOutlineColor": "#9a7bca",
    "selectedColor": "#9a7bca",
    "color": "#a791b4",
    "rollOverColor": "#9a7bca"
  },


  "dataProvider": continentsDataProvider,

  "listeners": [ {
    "event": "homeButtonClicked",
    "method": handleGoHome
  }, {
    "event": "clickMapObject",
    "method": handleMapObjectClick
  } ]

} );

function handleGoHome() {
  map.dataProvider = continentsDataProvider;
  map.validateNow();
}

function handleMapObjectClick( event ) {
  if ( event.mapObject.id == "backButton" ) {
    handleGoHome();
  }
}


